import React from 'react';
import { Routes, Route } from 'react-router-dom'; 
import './App.css'; 

import TodoList from './components/TodoList'; 
import Login from './components/Login'; Â  Â  Â  
import ProtectedRoute from './components/ProtectedRoute'; 

// Importa un componente para manejar errores (opcional pero recomendado)
import NotFound from './components/NotFound'; // Asume que tienes un componente NotFound

function App() {
  return (
    // ðŸš¨ PASO 1: ENVOLVEMOS TODO CON LOS ESTILOS DE CENTRADO Y FONDO
    <div className="login-page-container">
      {/* ðŸš¨ PASO 2: AÃ‘ADIMOS EL CONTENEDOR DE LA TARJETA 
          Le damos un ancho mÃ¡ximo un poco mayor para la lista, si es necesario. */}
      <div className="login-card" style={{ maxWidth: '600px', padding: '20px' }}>
      
        <Routes>
          
          {/* 1. GRUPO DE RUTAS PROTEGIDAS */}
          <Route element={<ProtectedRoute />}>
            {/* La ruta raÃ­z (/) ahora muestra la lista de tareas si el usuario estÃ¡ logueado */}
            <Route path="/" element={<TodoList />} /> 
            {/* Si tienes otras rutas protegidas (ej: /perfil) irÃ­an aquÃ­ */}
          </Route>
          
          {/* 2. RUTAS PÃšBLICAS */}
          {/* El Login ahora encaja directamente como contenido de la tarjeta */}
          <Route path="/login" element={<Login />} />
          
          {/* 3. RUTA CATCH-ALL (Si no encuentra ninguna de las anteriores) */}
          <Route path="*" element={<NotFound />} /> 
          
        </Routes>
        
      </div> {/* Fin de .login-card */}
    </div> /* Fin de .login-page-container */
  );
}

export default App;
