// C:\CamilaSerniotti-TPFinal\frontend\src\App.jsx

import React from 'react';
// IMPORTACIONES CR√çTICAS DE REACT-ROUTER-DOM
import { Routes, Route, Link } from 'react-router-dom'; 
import './App.css'; // Mantenemos los estilos generales aqu√≠

// IMPORTACI√ìN DE COMPONENTES DE P√ÅGINA
import TodoList from './components/TodoList'; 
import Login from './components/Login';       

function App() {
  return (
    <div className="App">
      {/* üéØ BARRA DE NAVEGACI√ìN PARA PRUEBA (¬°CR√çTICA!) */}
      <nav style={{ marginBottom: '20px', padding: '10px', backgroundColor: '#ecf0f1' }}>
        <Link to="/" style={{ marginRight: '15px', color: '#2980b9' }}>[Ver Agenda]</Link>
        <Link to="/login" style={{ color: '#2980b9' }}>[Ver Login]</Link>
      </nav>
      
      {/* üéØ DEFINICI√ìN DE RUTAS */}
      <Routes>
        {/* Ruta ra√≠z: Muestra la Agenda de Tareas */}
        <Route path="/" element={<TodoList />} /> 
        
        {/* Ruta /login: Muestra el formulario de inicio de sesi√≥n */}
        <Route path="/login" element={<Login />} />
        
        {/* Puedes a√±adir una ruta de registro si la tienes */}
        {/* <Route path="/register" element={<Register />} /> */}
      </Routes>
    </div>
  );
}

export default App;
