// src/components/Login.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import '../styles/Login.css'; // Mantenemos tu import de estilos

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook para la redirecci칩n

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // 丘멆잺 CR칈TICO: Reemplaza con la URL correcta de tu Backend si no es 4000.
      const API_URL = 'http://localhost:4000/api/v1/auth/login'; 
      
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      // 游뚿 PUNTO DE AJUSTE CLAVE 1:
      // Verifica si la respuesta HTTP fue exitosa (c칩digo 200-299)
      if (response.ok) { 
        
        // 游뚿 PUNTO DE AJUSTE CLAVE 2:
        // Aseg칰rate de que tu Backend est치 enviando el token
        // Tu backend deber칤a responder con: { token: 'JWT_STRING', user: {...} }
        if (data.token) {
          // 1. Guardar el token para mantener la sesi칩n
          localStorage.setItem('token', data.token);
          
          // 2. Redirigir al usuario a la Agenda
          // Si tu ruta de la agenda es el inicio, '/' est치 bien. Si es '/agenda' c치mbialo.
          navigate('/'); 
        } else {
            // El backend respondi칩 con 칠xito (200) pero no envi칩 el token esperado.
             setError('Error de servidor: Token no recibido.');
        }

      } else {
        // La respuesta HTTP no fue 200 (ej: 401 Unauthorized, 404 Not Found)
        // Usamos el mensaje del backend si existe.
        setError(data.message || 'Credenciales inv치lidas o error desconocido.');
      }

    } catch (err) {
      // Este catch maneja errores de red (e.g., el servidor Express est치 apagado)
      console.error(err);
      setError('No se pudo conectar con el servidor. 쮼st치 el backend corriendo?');
    }
  };

  // ... (El resto de tu JSX queda igual, no necesita cambios)

  return (
    // 游꿢 CLASE PRINCIPAL para el CSS del contenedor
    <div className="login-page-container"> 
      <div className="login-card">
        
        <h2 className="login-title">Login de Tareas</h2>
        <span className="close-btn" onClick={() => navigate('/')}>&times;</span>

        <form onSubmit={handleSubmit} className="login-form">
          {error && <p className="error-message">{error}</p>}
          
          <input 
            type="email" 
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input-field"
          />
          <input 
            type="password" 
            placeholder="Contrase침a"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="input-field"
          />

          <button type="submit" className="login-submit-button">
            Ingresar
          </button>
        </form>
        
        <div className="separator">O</div>

        {/* Botones Sociales */}
        <div className="social-login-container">
          <button className="social-button google-btn">
            <i className="fab fa-google"></i> Google
          </button>
          <button className="social-button github-btn">
            <i className="fab fa-github"></i> GitHub
          </button>
        </div>

        <p className="register-link">
          쯅o tienes cuenta? <a href="/register">Reg칤strate</a>
        </p>

      </div>
    </div>
  );
}

export default Login;
