// src/components/Login.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // 游뚿 NECESITAS ESTE IMPORT
// import axios from 'axios'; // O usas 'fetch' o usas 'axios', tu c칩digo anterior usaba 'fetch'
// Si usas 'fetch' no necesitas axios, si usas axios debes importarlo.

// NOTA: Si eliminaste los estilos de contenedor en App.jsx,
// este componente SOLO debe contener la tarjeta interior.

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // 游뚿 ESTO ES LO QUE TE PERMITE REDIRIGIR

  const handleSubmit = async (e) => {
    e.preventDefault(); // Detiene el refresco de la p치gina
    setError('');

    try {
      // 丘멆잺 CR칈TICO: Reemplaza con la URL correcta de tu Backend.
      const API_URL = 'http://localhost:4000/api/v1/auth/login'; 
      
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      // Verificar si la respuesta HTTP fue exitosa (200-299)
      if (response.ok) { 
        
        if (data.token) {
          // 1. Guardar el token para mantener la sesi칩n
          localStorage.setItem('token', data.token);
          
          // 2. Redirigir al usuario a la Agenda
          navigate('/'); // Redirige a la ruta protegida (TodoList)
        } else {
            setError('Error de servidor: Token no recibido.');
        }

      } else {
        // La respuesta HTTP no fue 200 (ej: 401 Unauthorized)
        setError(data.message || 'Credenciales inv치lidas o error desconocido.');
      }

    } catch (err) {
      console.error(err);
      setError('No se pudo conectar con el servidor. 쮼st치 el backend corriendo?');
    }
  };

  return (
    // 游뚿 NOTA IMPORTANTE: Si moviste los divs de centrado a App.jsx, esta es la estructura correcta.
    // Si NO los moviste, debes a침adir los divs .login-page-container y .login-card aqu칤.
    <> 
      <h2 className="login-title">Login de Tareas</h2>
      {/* El bot칩n de cerrar (X) solo funciona si est치s en una modal o si quieres volver a la ra칤z */}
      {/* <span className="close-btn" onClick={() => navigate('/')}>&times;</span> */}

      <form onSubmit={handleSubmit} className="login-form">
        {error && <p className="error-message">{error}</p>}
        
        <input 
          type="email" 
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          className="input-field"
        />
        <input 
          type="password" 
          placeholder="Contrase침a"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="input-field"
        />

        <button type="submit" className="login-submit-button">
          Ingresar
        </button>
      </form>
      
      {/* Resto de botones sociales / enlace de registro */}

    </>
  );
}

export default Login;
