// src/App.jsx

import React from 'react';
import { Routes, Route } from 'react-router-dom';
import './App.css'; // Mantenego el CSS importado

import TodoList from './components/TodoList';
import Login from './components/Login';
import ProtectedRoute from './components/ProtectedRoute';

// üö® COMPONENTE DE ERROR M√çNIMO: 
// Lo creamos aqu√≠ para evitar el error "Cannot find module './components/NotFound'"
const SimpleNotFound = () => (
  <div style={{ textAlign: 'center', padding: '50px' }}>
    <h2>¬°Error 404!</h2>
    <p>La p√°gina que buscas no existe.</p>
    <p>Por favor, usa el bot√≥n de "Ingresar" si ves esta p√°gina por error.</p>
  </div>
);


function App() {
  return (
    <div className="App">

      <Routes>

        {/* 1. GRUPO DE RUTAS PROTEGIDAS */}
        {/* El componente <ProtectedRoute> revisa si hay token antes de mostrar las rutas anidadas. */}
        <Route element={<ProtectedRoute />}>

          {/* A. Ruta ra√≠z (/) que muestra la Agenda de Tareas */}
          <Route path="/" element={<TodoList />} />

        </Route>

        {/* 2. RUTAS P√öBLICAS */}
        <Route path="/login" element={<Login />} />

        {/* 3. RUTA CATCH-ALL (404) */}
        {/* Usa el componente simple que definimos arriba */}
        <Route path="*" element={<SimpleNotFound />} />

      </Routes>
    </div>
  );
}

export default App;
