// C:\CamilaSerniotti-TPFinal\frontend\src\App.jsx

import React from 'react';
import { Routes, Route } from 'react-router-dom'; 
import './App.css'; 

import TodoList from './components/TodoList'; 
import Login from './components/Login'; 
import Register from './components/Register'; // ‚¨ÖÔ∏è ¬°CORRECCI√ìN 1: Importar el componente de Registro!
import ProtectedRoute from './components/ProtectedRoute'; 

function App() {
  return (
    <div className="App">
      
      <Routes>
        
        {/* 1. Definir la ruta protegida */}
        {/* Usamos un <Route> sin 'path' ni 'element' para agrupar las rutas hijas */}
        <Route element={<ProtectedRoute />}>
          {/* Todas las rutas definidas DENTRO de aqu√≠ estar√°n protegidas */}
          
          {/* A. Ruta ra√≠z (/) ahora protegida por ProtectedRoute */}
          <Route path="/" element={<TodoList />} /> 
        </Route>
        
        {/* 2. Definir rutas p√∫blicas */}
        <Route path="/login" element={<Login />} />
        
        {/* üéØ CORRECCI√ìN 2: A√±adir la ruta /register para evitar el error "No routes matched" */}
        <Route path="/register" element={<Register />} /> 
        
      </Routes>
    </div>
  );
}

export default App;
