// C:\CamilaSerniotti-TPFinal\frontend\src\App.jsx

import React from 'react';
import { Routes, Route } from 'react-router-dom'; 
import './App.css'; 

import TodoList from './components/TodoList'; 
import Login from './components/Login';       
import ProtectedRoute from './components/ProtectedRoute'; // ⬅️ Importar el guardián

function App() {
  return (
    <div className="App">
      
      <Routes>
        
        {/* 1. Definir la ruta protegida */}
        {/* Usamos un <Route> sin 'path' ni 'element' para agrupar las rutas hijas */}
        <Route element={<ProtectedRoute />}>
          {/* Todas las rutas definidas DENTRO de aquí estarán protegidas */}
          
          {/* A. Ruta raíz (/) ahora protegida por ProtectedRoute */}
          <Route path="/" element={<TodoList />} /> 
        </Route>
        
        {/* 2. Definir rutas públicas */}
        <Route path="/login" element={<Login />} />
        {/* Si tienes ruta de registro, también iría aquí: <Route path="/register" element={<Register />} /> */}
        
      </Routes>
    </div>
  );
}

export default App;
